mapper.update:
update t_emp
set emp_name = #{empName,jdbcType=VARCHAR},
    age      = #{age,jdbcType=INTEGER},
    gender   = #{gender,jdbcType=CHAR},
    dept_id  = #{deptId,jdbcType=INTEGER}
where emp_id = #{empId,jdbcType=INTEGER}

mapper.select:
select emp_id, emp_name, age, gender, dept_id
from t_emp
where emp_id = #{empId,jdbcType=INTEGER}
